// Start Page Scroll Controls
function toggleTopNav(direction) {
    if ( direction === 'up' && $(this).scrollTop() < 90) {
        if ( $('header').hasClass('reduced') ) {
            $('header').removeClass('reduced');
        }
    } else if (direction === 'down' && $(this).scrollTop() > 90) {
        $('header').addClass('reduced');
        $('.charComic').addClass('poppedUp');
    }
}


$(document).ready(function() {
    function scrollEvent(event) {
           var st = $(this).scrollTop();
           if (st > lastScrollTop){
               // downscroll code
               toggleTopNav('down');
           } else {
              // upscroll code
              toggleTopNav('up');
           }
           lastScrollTop = st;
    };
    var scrollTimeout;
    var throttle = 250;
    var lastScrollTop = 0;
    $(window).on('scroll', function () {
        if (!scrollTimeout) {
            scrollTimeout = setTimeout(function () {
                scrollEvent(this);
                scrollTimeout = null;
            }, throttle);
        }
    });
    
})